// TestContainer.js

import React, { useState } from "react";
import questionFlow from "./questionFlow";

function TestContainer() {
  const [step, setStep] = useState(0);
  const currentStep = questionFlow[step];

  const handleNext = () => {
    if (step < questionFlow.length - 1) {
      setStep((prev) => prev + 1);
    } else {
      alert("검사가 완료되었습니다.");
    }
  };

  return (
    <div style={{ padding: 24 }}>
      <h2>{currentStep.title}</h2>

      {currentStep.type === "basic" && (
        <ul>
          {currentStep.questions.map((q, idx) => (
            <li key={idx}>{q.text}</li>
          ))}
        </ul>
      )}

      {currentStep.type === "box" && (
        <ul>
          {currentStep.boxes.map((box) => (
            <li key={box.id}>{box.text}</li>
          ))}
        </ul>
      )}

      <button onClick={handleNext} style={{ marginTop: 24 }}>
        다음
      </button>
    </div>
  );
}

export default TestContainer;
